// 第二步 定义Route以及DefaultRoute
var Route = React.createFactory(ReactRouter.Route)
// 定义默认路由
var DefaultRoute = React.createFactory(ReactRouter.DefaultRoute)

// 第三步 定义规则，第一方式跟定义虚拟DOM方式是一模一样的
// 有两个属性，一个是path定义路由规则，一个是handler定义组件的
// 不是默认的页面组件，通过Route定义
var routes = (
	<Route path="/" handler={App}>
	 <Route path="start" handler={组件名}></Route>
	 <DefaultRoute handler={组件名} />
	</Route>
)

// 第四步 启动路由
ReactRouter.run(routes, function (Handler, params) {
	// 参数中path可以获取路由的路径，因此可以将他传递给父组件
	React.render(<Handler page={params.path}></Handler>,document.getElementById('app'))
})

// 解决单页面锚链接问题
scrollToAnchor(anchorName) {
if (anchorName) {
	// 找到锚点
	let anchorElement = document.getElementById(anchorName);
	// 如果对应id的锚点存在，就跳转到锚点
	if (anchorElement) {
	anchorElement.scrollIntoView();
	}
}
}

// 定义工具方法
let Util = {
	/**
	 * 发送异步请求
	 * @url 	请求的地址
	 * @fn 		请求成功时候的回调函数
	 **/
	ajax(url, fn) {
		// 实例化xhr对象
		let xhr = new XMLHttpRequest();
		// 监听状态变化
		xhr.onreadystatechange = () => {
			// 监听数据请求完毕
			if (xhr.readyState === 4) {
				// 判断状态码
				if (xhr.status === 200) {
					// 将数据转化成json字符串
					fn && fn(JSON.parse(xhr.responseText))
				}
			}
		}
		// 打开请求
		xhr.open('GET', url, true);
		// 发送数据
		xhr.send(null)
	},
	/**
	 * 将对象转化成query的形式
	 * @obj 	转化的对象 {color:red, num:100}
	 * return 	?color=red&num=100
	 **/
	objToQuery(obj) {
		let result = '';
		// 遍历对象，转化成json
		for (var i in obj) {
			result += '&' + i + '=' + obj[i]
		}
		// 去除最后一个&
		return '?' + result.slice(1)
	}
}
